<script type=""text/javascript"">

    $(document).ready(function () {
        $("".dinheiro"").maskMoney({
            prefix: ""R$ "",
            decimal: "","",
            thousands: "".""
        });
    });

    var productID = GetParameter();

    $('#category').select2({
        theme: ""classic"",
        allowClear: false
    });

    function GetParameter() {
        var _myUrl = window.location.search.substring(1).split('&');
        var _collection = _myUrl[0].split('=');
        return _collection[1];
    }

    function CategoryType(item) {
        this.id = ko.observable(item.categoryID);
        this.name = ko.observable(item.name);
    };

    $(document).ready(function () {
        $.getJSON('/Products/GetProductByID?productID=' + productID, function (data) {
            var vm = new ViewModel(data);
            // ativa knockout.js
            ko.applyBindings(vm);
        });
    });

    function ViewMod");
            WriteLiteral(@"el(data) {
        try {

            var self = this;

            self.category = ko.observableArray([]);
